import { useEffect, useState } from "react";
import { supabase } from '../supabaseClient'; // Adjust path if needed

export default function RoleForm() {
  // Form fields
  const [teamName, setTeamName] = useState("");
  const [description, setDescription] = useState("");
  const [administrator, setAdministrator] = useState("no"); // default no

  // State
  const [roles, setRoles] = useState([]);
  const [editingId, setEditingId] = useState(null);

  // -------------------------------
  // CREATE
  const addRole = async () => {
    const newRole = {
      team_name: teamName,
      description,
      administrator,
    };

    const { data, error } = await supabase
      .from("role")
      .insert([newRole])
      .select()
      .single();

    if (error) {
      console.error("Error adding role:", error);
      alert("Error creating role");
    } else {
      setRoles([...roles, data]);
      clearForm();
    }
  };

  // -------------------------------
  // READ
  useEffect(() => {
    listRoles();
  }, []);

  const listRoles = async () => {
    const { data, error } = await supabase.from("role").select("*");
    if (error) {
      console.error("Error fetching roles:", error);
      alert("No data");
    } else {
      setRoles(data);
    }
  };

  // -------------------------------
  // DELETE
  const deleteRole = async (id) => {
    const { error } = await supabase.from("role").delete().eq("id", id);
    if (error) {
      console.error("Error deleting role:", error);
    } else {
      setRoles(roles.filter((role) => role.id !== id));
    }
  };

  // -------------------------------
  // EDIT
  const editRole = (role) => {
    setEditingId(role.id);
    setTeamName(role.team_name);
    setDescription(role.description);
    setAdministrator(role.administrator);
  };

  // -------------------------------
  // UPDATE
  const updateRole = async () => {
    const { data, error } = await supabase
      .from("role")
      .update({
        team_name: teamName,
        description,
        administrator,
      })
      .eq("id", editingId)
      .select()
      .single();

    if (error) {
      console.error("Error updating role:", error);
      alert("Error updating role");
    } else {
      setRoles(roles.map((role) => (role.id === editingId ? data : role)));
      clearForm();
    }
  };

  // -------------------------------
  const clearForm = () => {
    setEditingId(null);
    setTeamName("");
    setDescription("");
    setAdministrator("no");
  };

  // -------------------------------
  return (
    <div className="container-xl">
      <div className="row">

        {/* Form */}
        <div className="col-lg-6 col-md-6 col-sm-12">
          <div className="card card-registration my-4">
            <div className="row g-0">
              <div className="col-xl-12">
                <div className="card-body p-md-5 text-black">
                  <h3 className="mb-5 text-uppercase">Role Form</h3>

                  {/* Team Name */}
                  <div className="form-outline mb-4">
                    <input
                      type="text"
                      value={teamName}
                      onChange={(e) => setTeamName(e.target.value)}
                      placeholder="Team Name"
                      className="form-control form-control-lg border-secondary"
                    />
                  </div>

                  {/* Description */}
                  <div className="form-outline mb-4">
                    <textarea
                      value={description}
                      onChange={(e) => setDescription(e.target.value)}
                      placeholder="Description"
                      className="form-control form-control-lg border-secondary"
                      rows="3"
                    ></textarea>
                  </div>

                  {/* Administrator */}
                  <label className="form-label">Administrator</label>
                  <div className="d-md-flex mb-4">
                    <div className="form-check form-check-inline me-4">
                      <input
                        className="form-check-input border-secondary"
                        type="radio"
                        value="yes"
                        checked={administrator === "yes"}
                        onChange={(e) => setAdministrator(e.target.value)}
                      />
                      <label className="form-check-label">Yes</label>
                    </div>
                    <div className="form-check form-check-inline me-4">
                      <input
                        className="form-check-input border-secondary"
                        type="radio"
                        value="no"
                        checked={administrator === "no"}
                        onChange={(e) => setAdministrator(e.target.value)}
                      />
                      <label className="form-check-label">No</label>
                    </div>
                  </div>

                  {/* Buttons */}
                  <div className="d-flex justify-content-end pt-3">
                    <button type="button" className="btn btn-light btn-lg me-2" onClick={clearForm}>
                      Clear
                    </button>
                    <button
                      type="button"
                      className="btn btn-primary btn-lg"
                      onClick={editingId ? updateRole : addRole}
                    >
                      {editingId ? "Update Role" : "Add Role"}
                    </button>
                  </div>

                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Roles Table */}
        <div className="col-lg-6 col-md-6 col-sm-12">
          <div className="table-responsive">
            <br />
            <table className="table table-striped table-hover table-bordered">
              <thead>
                <tr>
                  <th>#</th>
                  <th>Team Name</th>
                  <th>Description</th>
                  <th>Administrator</th>
                  <th></th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {roles.map((role) => (
                  <tr key={role.id}>
                    <td>{role.id}</td>
                    <td>{role.team_name}</td>
                    <td>{role.description}</td>
                    <td>
                      <span className={`badge ${role.administrator === "yes" ? "bg-success" : "bg-secondary"}`}>
                        {role.administrator}
                      </span>
                    </td>
                    <td>
                      <button type="button" className="btn btn-info btn-sm" onClick={() => editRole(role)}>
                        Edit
                      </button>
                    </td>
                    <td>
                      <button type="button" className="btn btn-danger btn-sm" onClick={() => deleteRole(role.id)}>
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

      </div>
    </div>
  );
}
