import { useState } from "react";
import { Link } from "react-router-dom";
import { supabase } from "../supabaseClient";

export default function UserForm() {

  const [fname, setNewFname] = useState("");
  const [lname, setNewLname] = useState("");
  const [mail, setNewMail] = useState("");
  const [password, setNewPassword] = useState("");

  /* Create User */
  const addUser = async () => {

    if (!fname || !lname || !mail || !password) {
      alert("All fields are required");
      return;
    }

    const { data, error } = await supabase
      .from("users")
      .insert([
        {
          fname: fname,
          lname: lname,
          email: mail, // Asegúrate que en la DB sea "email"
          password: password,
          is_complete: false // ⚠️ usa el nombre EXACTO de tu columna
        }
      ])
      .select();

    if (error) {
      console.error("Insert error:", error.message);
      alert(error.message);
    } else {
      console.log("New user:", data);

      // limpiar formulario
      setNewFname("");
      setNewLname("");
      setNewMail("");
      setNewPassword("");

      alert("User created successfully");
    }
  };

  return (
    <div className="container-xl">
      <div className="row">

        {/* FORM */}
        <div className="col-lg-6 col-md-6 col-sm-12">
          <div className="card card-registration my-4">
            <div className="card-body p-md-5 text-black">
              <h3 className="mb-5 text-uppercase">User form</h3>

              <input
                type="text"
                value={fname}
                onChange={(e) => setNewFname(e.target.value)}
                placeholder="First Name"
                className="form-control form-control-lg border-secondary mb-3"
              />

              <input
                type="text"
                value={lname}
                onChange={(e) => setNewLname(e.target.value)}
                placeholder="Last Name"
                className="form-control form-control-lg border-secondary mb-3"
              />

              <input
                type="email"
                value={mail}
                onChange={(e) => setNewMail(e.target.value)}
                placeholder="Email"
                className="form-control form-control-lg border-secondary mb-3"
              />

              <input
                type="password"
                value={password}
                onChange={(e) => setNewPassword(e.target.value)}
                placeholder="Password"
                className="form-control form-control-lg border-secondary mb-3"
              />

              <div className="d-flex justify-content-end pt-3">
                <Link to="/user" className="btn btn-light btn-lg">
                  Clear
                </Link>

                <button
                  onClick={addUser}
                  type="button"
                  className="btn btn-primary btn-lg ms-2"
                >
                  Submit form
                </button>
              </div>

            </div>
          </div>
        </div>

      </div>
    </div>
  );
}
